class Arma:
    def __init__(self):
        self.bullet: int = 25
        self.bulletOut: int = 75

    def __str__(self):
        return f"Arma com {self.bullet} balas, ainda restam {self.bulletOut} no pente"

    def atirar(self):
        if self.bullet == 0:
            print("fail: sem balas")
            return
        self.bullet -= 1

    def recarregar(self, decrement: int):
        if self.bullet == 25:
            print("fail: pente cheio")
            return
        if self.bullet < 25:
            decrement = 25 - self.bullet
            self.bulletOut -= decrement
            return
        self.bullet = 25

def main():
    arma = Arma()
    while True:
        line = input()
        print("$" + line)
        args: list[str] = line.split()

        if args[0] == "end":
            break
        elif args[0] == "show":
            print(arma)
        elif args[0] == "shoot":
            arma.atirar()


main()
