class Relogio:
    def __init__(self, hora: int, minuto: int, segundo: int):
        self.__hora: int = hora
        self.__min: int = minuto
        self.__seg: int = segundo

    def __str__(self):
        return f"{self.__hora:02d}:{self.__min:02d}:{self.__seg:02d}"

    def getHora(self) -> int:
        return self.__hora

    def getMin(self) -> int:
        return self.__min

    def getSec(self) -> int:
        return self.__seg

    def setHora(self,value: int):
        if value > 23 or value < 0:
            print("fail: hora invalido")
            return
        self.__hora = value

    def setMin(self,value: int):
        if value > 59 or value < 0:
            print("fail: minuto invalido")
            return
        self.__min = value

    def setSeg(self,value: int):
        if value > 59 or value < 0:
            print("fail: segundo invalido")
            return
        self.__seg = value

